// CSS 변수에 대한 전역 타입 선언
declare namespace CSS {
  interface PropertyDefinition {
    '--ls-primary-background-color': any;
    '--ls-border-color': any;
    '--ls-primary-text-color': any;
    '--ls-secondary-text-color': any;
    '--ls-secondary-background-color': any;
    '--ls-tertiary-background-color': any;
    '--ls-active-primary-color': any;
    '--ls-selection-background-color': any;
    '--ls-selection-text-color': any;
  }
}
